import React, { useState } from 'react';

const ColorDropdown = () => {
    // Step 2: Define the color array
    const colors = ['Red', 'Green', 'Blue', 'Yellow', 'Purple'];

    // Step 1: Set up the React component state
    const [selectedColors, setSelectedColors] = useState([]);

    // Step 4: Handle the selection of colors
    const handleColorChange = (event) => {
        const selectedColor = event.target.value;
        if (!selectedColors.includes(selectedColor)) {
            setSelectedColors([...selectedColors, selectedColor]);
        }
    };

    return (
        <div>
            {/* Step 3: Create a dropdown menu */}
            <select onChange={handleColorChange} defaultValue="">
                <option value="" disabled>Select a color</option>
                {colors.map((color, index) => (
                    <option key={index} value={color}>
                        {color}
                    </option>
                ))}
            </select>

            {/* Step 5: Display selected colors */}
            <div style={{ marginTop: '20px' }}>
                <h3>Selected Colors:</h3>
                <ul>
                    {selectedColors.map((color, index) => (
                        <li key={index} style={{ color: color }}>
                            {color}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default ColorDropdown;
