import React, { useState } from 'react';

const ColorDropdown = () => {
    // Define an array of colors with their hex values
    const colors = [
        { name: 'Red', hex: '#FF0000' },
        { name: 'Green', hex: '#00FF00' },
        { name: 'Blue', hex: '#0000FF' },
        { name: 'Yellow', hex: '#FFFF00' },
        { name: 'Purple', hex: '#800080' },
    ];

    // State to manage selected color
    const [selectedColor, setSelectedColor] = useState(null);

    // Handle the selection of a color
    const handleColorSelect = (color) => {
        setSelectedColor(color);
    };

    return (
        <div>
            {/* Display selected color or placeholder */}
            <div style={{ marginBottom: '10px', fontSize: '16px' }}>
                Selected Color: {selectedColor ? selectedColor.name : 'None'}
            </div>

            {/* Custom dropdown */}
            <div style={{ position: 'relative', display: 'inline-block' }}>
                <div
                    style={{
                        backgroundColor: selectedColor ? selectedColor.hex : '#ffffff',
                        width: '100px',
                        height: '30px',
                        border: '1px solid #ccc',
                        cursor: 'pointer',
                    }}
                    onClick={() => document.getElementById('color-dropdown').classList.toggle('show')}
                ></div>

                {/* Dropdown menu */}
                <div
                    id="color-dropdown"
                    style={{
                        display: 'none',
                        position: 'absolute',
                        backgroundColor: '#f9f9f9',
                        boxShadow: '0px 8px 16px 0px rgba(0,0,0,0.2)',
                        zIndex: 1,
                        width: '100px',
                    }}
                >
                    {colors.map((color, index) => (
                        <div
                            key={index}
                            onClick={() => {
                                handleColorSelect(color);
                                document.getElementById('color-dropdown').classList.remove('show');
                            }}
                            style={{
                                backgroundColor: color.hex,
                                width: '100px',
                                height: '30px',
                                cursor: 'pointer',
                            }}
                        ></div>
                    ))}
                </div>
            </div>

            {/* Output the selected color */}
            <div style={{ marginTop: '20px', fontSize: '16px' }}>
                Output: {selectedColor ? selectedColor.hex : 'No color selected'}
            </div>

            <style>{`
        #color-dropdown.show {
          display: block;
        }
      `}</style>
        </div>
    );
};

export default ColorDropdown;
